import { Outlet, Link } from "react-router-dom";
import '../styles/main.css';
import '../styles/menu.css';

const Layout = () => {
  return (
    <>
	    <body>
		    <div class="header">
			    <a href="/">Accueil</a>
			    <h1>Apprendre le Japonais</h1>
			    <Link to="/aide">Aide</Link>
		    </div>
        </body>

      <Outlet />

      <div class="footer">
			    <p>C. Jordan - M. Ruffieux - M2 MIASHS - 2022/2023</p>
		    </div>
    </>
  )
};

export default Layout;
